function FilterLine(props) {
  return (
    <div>
      <input
        type="checkbox"
        onClick={() => {
          console.log("click");
          if (props.state[0].includes(props.text)) {
            props.state[1](
              props.state[0].filter((value) => value === props.text)
            );
          } else {
            props.state[1]([...props.state[0]], props.text);
          }
        }}
      />
      {props.text}
    </div>
  );
}

export default FilterLine;
