import React from "react";
import CheckBoxFilterBlock from "./CheckBoxFilterBlock";
import "./Filters.css";

function Filters() {
  const [sizes, setSizes] = React.useState([]);
  const onSizeChange = (isChecked, value) => {
    if (isChecked) {
      setSizes(...sizes, value);
    } else {
      setSizes(sizes.filter((item) => item !== value));
    }
  };

  return (
    <div className="filtersContainer">
      Фильтры
      <CheckBoxFilterBlock
        name="Размеры"
        data={sizesFilterData}
        state={sizes}
        onChange={onSizeChange}
      ></CheckBoxFilterBlock>
    </div>
  );
}

const sizesFilterData = [
  { key: 0, text: "S" },
  { key: 1, text: "M" },
  { key: 2, text: "X" },
  { key: 3, text: "XS" },
];

export default Filters;
