import "./styles.css";
import React, { Component } from "react";
import {
  AiOutlineCheckSquare,
  AiOutlineUser,
  AiOutlineBook,
  AiOutlineCoffee,
  AiOutlineDesktop,
} from "react-icons/ai";

export class SideNavigationMenu extends Component {
  constructor(props) {
    super(props);
    this.state = { type: "Все" };
  }

  onTypeSelected = (item) => {
    this.setState({ type: item.value });
    console.log(JSON.stringify(this.state));
  };

  render() {
    return (
      <div className="sideNavigationMenuContainer">
        Вкладки
        {typesData.map((item) => (
          <div
            className="sideNavigationMenuItemContainer"
            key={item.key}
            onClick={this.onTypeSelected}
          >
            {<item.icon />} {item.value}
          </div>
        ))}
      </div>
    );
  }
}

const typesData = [
  { key: 0, value: "Все", icon: AiOutlineCheckSquare, isSelected: true },
  { key: 1, value: "Личное", icon: AiOutlineUser, isSelected: false },
  { key: 2, value: "Работа", icon: AiOutlineDesktop, isSelected: false },
  { key: 3, value: "Учеба", icon: AiOutlineBook, isSelected: false },
  { key: 4, value: "Отдых", icon: AiOutlineCoffee, isSelected: false },
];

export default SideNavigationMenu;
